// https://github.com/elias8auer/Scrollengine/
class Scrollengine{constructor({element=window,strength=15,acceleration=1.2,deceleration=0.975}={}){this.element=element;this.distance=strength;this.acceleration=acceleration;this.deceleration=deceleration;this.running=!1;this.element.addEventListener('wheel',this.scrollHandler.bind(this),{passive:!1});this.element.addEventListener('mousewheel',this.scrollHandler.bind(this),{passive:!1});this.scroll=this.scroll.bind(this)}
scrollHandler(e){e.preventDefault();if(!this.running){this.top=this.element.pageYOffset||this.element.scrollTop||0;this.running=!0;this.currentDistance=e.deltaY>0?0.1:-0.1;this.isDistanceAsc=!0;this.scroll()}else{this.isDistanceAsc=!1;this.currentDistance=e.deltaY>0?this.distance:-this.distance}}
scroll(){if(this.running){this.currentDistance*=this.isDistanceAsc===!0?this.acceleration:this.deceleration;this.top+=this.currentDistance;this.element.scrollTo(0,this.top);requestAnimationFrame(this.scroll)}}}
export default Scrollengine
